package per.jaga.learn.loop;

public class ifLoop {

	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 70;
		
		if (age>=18 && age<=70)
		{
			System.out.println("Result: Eligible to vote!");
		}
		
		else
		{
			System.out.println("Not eligible to vote!");
		}

	}

}
